mapper.select:
		select `id`,`topic_type`,`fraction`,`title_number`,`subject`,`topic_options`,`question_answer` from item_bank

		  		   and id = #{id} 
		  		   and topic_type = #{topicType} 
			       and title_number = #{titleNumber} 
		  		   and fraction = #{fraction} 
		  		   and subject = #{subject} 
		  		   and topic_options = #{topicOptions} 
		  		   and question_answer = #{questionAnswer} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from item_bank

  		   and id = #{id} 
  		   and topic_type = #{topicType} 
	       and title_number = #{titleNumber} 
  		   and fraction = #{fraction} 
  		   and subject = #{subject} 
  		   and topic_options = #{topicOptions} 
  		   and question_answer = #{questionAnswer}
