mapper.select:
		select id,topic_type,candidate_name,total_score,examination_time,title_number,subject,question_answer,choose_answer,
final_score,
score_per_question from test_paper where id = #{value}

		select id,topic_type,candidate_name,total_score,examination_time,title_number,subject,question_answer,choose_answer,
final_score,
score_per_question from test_paper

		  		   and id = #{id} 
		  		   and topic_type = #{topicType} 
		  		   and candidate_name = #{candidateName} 
		  		   and total_score = #{totalScore} 
		  		   and examination_time = #{examinationTime} 
		  		   and title_number = #{titleNumber} 
		  		   and subject = #{subject} 
		  		   and question_answer = #{questionAnswer} 
		  		   and choose_answer = #{chooseAnswer} 
		  		   and 
final_score = #{
finalScore} 
		  		   and 
score_per_question = #{
scorePerQuestion} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from test_paper

		  		   and id = #{id} 
		  		   and topic_type = #{topicType} 
		  		   and candidate_name = #{candidateName} 
		  		   and total_score = #{totalScore} 
		  		   and examination_time = #{examinationTime} 
		  		   and title_number = #{titleNumber} 
		  		   and subject = #{subject} 
		  		   and question_answer = #{questionAnswer} 
		  		   and choose_answer = #{chooseAnswer} 
		  		   and 
final_score = #{
finalScore} 
		  		   and 
score_per_question = #{
scorePerQuestion}

mapper.update:
update test_paper
topic_type = #{topicType}, candidate_name = #{candidateName}, total_score = #{totalScore}, examination_time = #{examinationTime}, title_number = #{titleNumber}, subject = #{subject}, question_answer = #{questionAnswer}, choose_answer = #{chooseAnswer},
final_score = #{ finalScore},
score_per_question = #{ scorePerQuestion}
where id = #{id}
